import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ReservationPage.css';

const ReservationPage = () => {
  const { state } = useLocation(); // Access the slot and location passed from SlotDetailsPage
  const navigate = useNavigate();

  const [user, setUser] = useState(null); // State to store user data
  const [vehicleNumber, setVehicleNumber] = useState(''); // State to store selected vehicle number
  const [isLoading, setIsLoading] = useState(false); // State to handle loading during API calls
  const [error, setError] = useState(''); // State to handle errors
  const [paymentProcessing, setPaymentProcessing] = useState(false); // State to manage payment processing state

  // Fetch user profile on component mount
  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login'); // Redirect to login if no token is found
          return;
        }

        const response = await axios.get('http://localhost:5000/routes/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(response.data.user); // Set user data
      } catch (error) {
        console.error('Error fetching user profile:', error);
        setError('Failed to fetch user profile. Please try again.');
        navigate('/login'); // Redirect to login on error
      }
    };

    fetchUserProfile();
  }, [navigate]);

  // Early return for loading state or missing state data
  if (!state || !state.slot || !state.location) {
    return <div>Loading slot details...</div>;
  }

  const { slot, location } = state;
  console.log(slot);

  // Handle reservation submission
  const handleSubmit = async () => {
    if (!vehicleNumber) {
      setError('Please select a vehicle.');
      return;
    }

    setIsLoading(true); // Set loading state
    setError(''); // Clear previous errors

    try {
      const reservationData = {
        user_id: user.id, // Sending user_id from the fetched user data
        slot: {
          slot_id: slot.id, // Send both slot_id and area_id
          area_id: location.id,
        },
        location: location.name,
        vehicleNumber,
      };
      console.log(reservationData);

      // Call a mock payment function here before proceeding with reservation
      const paymentSuccessful = await handlePayment();

      if (paymentSuccessful) {
        // Send the reservation data to the backend
        const response = await axios.post(
          'http://localhost:5000/api/reservations/reserve',
          reservationData
        );

        if (response.status === 201) {
          alert('Reservation and payment successful!');
          navigate('/dashboard'); // Redirect to dashboard after successful reservation
        }
      } else {
        setError('Payment failed. Please try again.');
      }
    } catch (error) {
      console.error('Error during reservation:', error);
      setError(error.response?.data?.error || 'Error during reservation. Please try again.');
    } finally {
      setIsLoading(false); // Reset loading state
    }
  };

  // Mock payment function
  const handlePayment = async () => {
    try {
      setPaymentProcessing(true);
      // Simulate payment processing logic (e.g., using a payment gateway like Stripe)
      // Replace this with actual payment gateway integration
      await new Promise((resolve) => setTimeout(resolve, 2000)); // Simulate a delay for payment processing
      setPaymentProcessing(false);
      return true; // Simulate successful payment
    } catch (error) {
      setPaymentProcessing(false);
      return false; // Simulate payment failure
    }
  };

  return (
    <div className="reservation-page">
      <h2>Reserve Slot {slot.name} at {location.name}</h2>

      <div className="reservation-box">
        {/* User Info */}
        {user ? (
          <div className="user-info">
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </div>
        ) : (
          <div>Loading user data...</div>
        )}

        {/* Slot and Location Info */}
        <div className="slot-location-info">
          <p><strong>Slot Location:</strong> {location.name}</p>
          <p><strong>Slot Number:</strong> {slot.name}</p>
        </div>

        {/* Vehicle Number Selection */}
        {user && (
          <div>
            <label htmlFor="vehicleNumber">Select Vehicle</label>
            <select
              id="vehicleNumber"
              value={vehicleNumber}
              onChange={(e) => setVehicleNumber(e.target.value)}
              disabled={isLoading || paymentProcessing} // Disable dropdown during loading or payment processing
            >
              <option value="">--Select Vehicle--</option>
              {user.VehicleNumber1 && <option value={user.VehicleNumber1}>{user.VehicleNumber1}</option>}
              {user.VehicleNumber2 && <option value={user.VehicleNumber2}>{user.VehicleNumber2}</option>}
              {user.VehicleNumber3 && <option value={user.VehicleNumber3}>{user.VehicleNumber3}</option>}
            </select>
          </div>
        )}

        {/* Error Message */}
        {error && <div className="error-message">{error}</div>}

        {/* Pay and Reserve Button */}
        <button onClick={handleSubmit} disabled={isLoading || paymentProcessing}>
          {paymentProcessing ? 'Processing Payment...' : 'Pay and Reserve'}
        </button>
      </div>
    </div>
  );
};

export default ReservationPage;
