import React, { useState } from "react";

const SlotBookingPage = () => {
  const locations = [
    { name: "GITAM Bengaluru", vacant: 15, reserved: 10, occupied: 20 },
    { name: "GITAM Hyderabad", vacant: 25, reserved: 5, occupied: 30 },
    { name: "GITAM Vizag", vacant: 20, reserved: 10, occupied: 15 },
  ];

  const [selectedLocation, setSelectedLocation] = useState(null);

  const handleLocationClick = (location) => {
    setSelectedLocation(location);
  };

  return (
    <div className="slot-booking-page">
      <h2>Choose Your Location</h2>
      <div className="locations-list">
        {locations.map((location, index) => (
          <div
            key={index}
            className={`location-card ${selectedLocation === location ? "selected" : ""}`}
            onClick={() => handleLocationClick(location)}
          >
            <div className="location-name">{location.name}</div>
            <div className="slot-info">
              <div className="slot-field">
                <strong>Vacant:</strong> {location.vacant}
              </div>
              <div className="slot-field">
                <strong>Reserved:</strong> {location.reserved}
              </div>
              <div className="slot-field">
                <strong>Occupied:</strong> {location.occupied}
              </div>
            </div>
          </div>
        ))}
      </div>
      {selectedLocation && (
        <div className="location-info">
          <p>
            You have selected {selectedLocation.name}. Here are the slot details:
          </p>
          <div className="slot-details">
            <div className="slot-detail">
              <label>Vacant</label>
              <p>{selectedLocation.vacant}</p>
            </div>
            <div className="slot-detail">
              <label>Reserved</label>
              <p>{selectedLocation.reserved}</p>
            </div>
            <div className="slot-detail">
              <label>Occupied</label>
              <p>{selectedLocation.occupied}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SlotBookingPage;
