import React, { useState } from "react";
import "./HomePage.css";
import SignupPage from "./Signup";
import LoginPage from "./Login";
import WelcomeImage from "../images/img1.webp"; // Replace with your image path
import Logo from "../images/logo.webp"; // Replace with your logo path

const HomePage = () => {
  const [activePage, setActivePage] = useState(null);

  const renderContent = () => {
    if (activePage === "login") {
      return <LoginPage />;
    } else if (activePage === "signup") {
      return <SignupPage />;
    } else {
      return (
        <div className="welcome-section">
          <div className="welcome-text">
            <h1>Welcome to SmartPark</h1>
            <p>Effortless Parking at Your Fingertips</p>
            <ul>
              <li><strong>Real-Time Parking Availability:</strong> Instantly view available parking spots around your destination.</li>
              <li><strong>Advance Reservations:</strong> Reserve your parking space ahead of time to save effort and avoid last-minute stress.</li>
              <li><strong>Guided Navigation:</strong> Get step-by-step guidance to seamlessly reach your reserved spot.</li>
            </ul>
            <p>Join the revolution in parking managementâ€”saving time, reducing hassle, and making parking smarter.</p>
            <button onClick={() => setActivePage("signup")}>Get Started</button>
          </div>
          <div className="welcome-image">
            <img src={WelcomeImage} alt="Smart Parking" />
          </div>
        </div>
      );
    }
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <img src={Logo} alt="SmartPark Logo" className="navbar-logo" />
        <div className="navbar-links">
          <button
            className={`navbar-btn ${activePage === null ? "active" : ""}`}
            onClick={() => setActivePage(null)}
          >
            Home
          </button>
          <button
            className={`navbar-btn ${activePage === "login" ? "active" : ""}`}
            onClick={() => setActivePage("login")}
          >
            Login
          </button>
          <button
            className={`navbar-btn ${activePage === "signup" ? "active" : ""}`}
            onClick={() => setActivePage("signup")}
          >
            Signup
          </button>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default HomePage;
