import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './ActiveBookingsPage.css';

const ActiveBookings = () => {
  const [activeBookings, setActiveBookings] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    console.log('Fetching active bookings...');

    const fetchActiveBookings = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get('http://localhost:5000/routes/activebooking', {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log('Raw response from API:', response.data);

        if (Array.isArray(response.data)) {
          setActiveBookings(response.data);
        } else {
          console.error('Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('Error fetching active bookings:', error);
      }
    };

    fetchActiveBookings();
  }, [navigate]);

  useEffect(() => {
    console.log('Updated active bookings:', activeBookings);
  }, [activeBookings]);

  const handleExtend = (bookingId) => {
    setActiveBookings((prevBookings) =>
      prevBookings.map((booking) =>
        booking.id === bookingId ? { ...booking, remainingTime: booking.remainingTime + 1800 } : booking
      )
    );
  };

  const handleCancel = async (bookingId) => {
    try {
      await axios.delete(`http://localhost:5000/api/reservations/${bookingId}`);
      setActiveBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== bookingId));
    } catch (error) {
      console.error('Error canceling the booking:', error);
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  return (
    <div className="active-bookings">
      <h2>Active Bookings</h2>
      {activeBookings.length === 0 ? (
        <p>No active bookings</p>
      ) : (
        activeBookings.map((booking) => (
          <div key={booking.id} className="booking-card">
            <h3>Booking ID: {booking.id}</h3>
            <p><strong>Slot:</strong> {booking.slot.name}</p>
            <p><strong>Location:</strong> {booking.location}</p>
            <p><strong>Vehicle:</strong> {booking.vehicleNumber}</p>
            <p><strong>Remaining Time:</strong> {formatTime(booking.remainingTime)}</p>
            <button onClick={() => handleExtend(booking.id)}>Extend</button>
            <button onClick={() => handleCancel(booking.id)}>Cancel</button>
          </div>
        ))
      )}
    </div>
  );
};

export default ActiveBookings;
