import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SlotDetailsPage.css';

const SlotDetailsPage = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  // Check if location and user are defined
  if (!state || !state.location || !state.user) {
    return <div>Loading...</div>; // Or display an error message
  }

  const { location, user } = state; // Destructure location and user

  const slots = [
    { name: 'S1', state: 1 },
    { name: 'S2', state: 1 },
    { name: 'S3', state: 2 },
    { name: 'S4', state: 0 }
  ];

  const getSlotColor = (state) => {
    switch (state) {
      case 0: return 'green';
      case 1: return 'grey';
      case 2: return 'red';
      default: return 'white';
    }
  };

  const handleSlotClick = (slot) => {
    if (slot.state === 0) {
      navigate("/reservation", { 
        state: { 
          slot, 
          location, 
          vehicles: user ? [user.VehicleNumber1, user.VehicleNumber2, user.VehicleNumber3] : [] 
        }
      });
    }
  };

  return (
    <div className="slot-details-page">
      <h2>Slots for {location.name}</h2>
      <div className="slots-container">
        {slots.map((slot, index) => (
          <div
            key={index}
            className="slot"
            style={{ backgroundColor: getSlotColor(slot.state) }}
            onClick={() => handleSlotClick(slot)} // Trigger slot click
          >
            <h3>{slot.name}</h3>
            <p>{slot.state === 0 ? 'Available' : slot.state === 1 ? 'Reserved' : 'Occupied'}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SlotDetailsPage;
