import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './BookingHistoryPage.css';

const BookingHistoryPage = () => {
  const [bookingHistory, setBookingHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetching the user ID (Assuming it's stored in localStorage or context)
  const userId = localStorage.getItem('id'); 
  console.log(userId)
  useEffect(() => {
    if (userId) {
      const fetchBookingHistory = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/routes/booking-history/${userId}`);
          setBookingHistory(response.data); // Update state with booking history
        } catch (err) {
          setError(err.message); // Handle error
        } finally {
          setLoading(false); // Stop loading
        }
      };

      fetchBookingHistory();
    } else {
      setError('User not logged in');
      setLoading(false);
    }
  }, [userId]);

  if (loading) return <div className="loading">Loading booking history...</div>;

  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="booking-history-page">
      <h2>Your Booking History</h2>
      <div className="history-list">
        {bookingHistory.length > 0 ? (
          bookingHistory.map((booking, index) => (
            <div key={index} className="booking-card">
              <h3>Booking ID: {booking.id}</h3>
              <p>Location: {booking.location}</p>
              <p>Slot: {booking.slot}</p>
              <p>Status: {booking.status}</p>
              <p>Reservation Time: {new Date(booking.reservation_time).toLocaleString()}</p>
            </div>
          ))
        ) : (
          <p>No bookings found.</p>
        )}
      </div>

      <button className="back-button" onClick={() => navigate(-1)}>
        Back
      </button>
    </div>
  );
};

export default BookingHistoryPage;
