import React, { useState } from "react";
import "./HomePage.css";
import SignupPage from "./SignupPage";
import LoginPage from "./LoginPage";

const HomePage = () => {
  const [activePage, setActivePage] = useState("login"); // Default to login

  const renderContent = () => {
    if (activePage === "login") {
      return <LoginPage />;
    } else if (activePage === "signup") {
      return <SignupPage />;
    }
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <h1 className="navbar-logo">MyApp</h1>
        <div className="navbar-links">
          <button
            className={`navbar-btn ${activePage === "login" ? "active" : ""}`}
            onClick={() => setActivePage("login")}
          >
            Login
          </button>
          <button
            className={`navbar-btn ${activePage === "signup" ? "active" : ""}`}
            onClick={() => setActivePage("signup")}
          >
            Signup
          </button>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default HomePage;
