import React, { useState } from 'react';
import './SlotBookingPage.css';

const SlotBookingPage = () => {
  const [selectedLocation, setSelectedLocation] = useState(null);

  const locations = [
    { name: 'GITAM Bengaluru', id: 1 },
    { name: 'GITAM Hyderabad', id: 2 },
    { name: 'GITAM Vizag', id: 3 }
  ];

  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
  };

  return (
    <div className="slot-booking-page">
      <h2>Select your location for slot booking</h2>
      <div className="locations-list">
        {locations.map((location) => (
          <div
            key={location.id}
            className={`location-card ${selectedLocation === location ? 'selected' : ''}`}
            onClick={() => handleLocationSelect(location)}
          >
            {location.name}
          </div>
        ))}
      </div>
      {selectedLocation && (
        <div className="location-info">
          <h3>You selected: {selectedLocation.name}</h3>
          {/* You can add the next steps for the user to book a slot here */}
        </div>
      )}
    </div>
  );
};

export default SlotBookingPage;
