import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId"); // Retrieve userId

    if (!token || !userId) {
      navigate("/login"); // Redirect if missing token or userId
      return;
    }

    axios
      .get(`http://localhost:5000/routes/profile/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data.user);
      })
      .catch((error) => {
        console.error("Error fetching profile:", error);
        localStorage.removeItem("token");
        localStorage.removeItem("userId");
        navigate("/login");
      });
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  if (!user) {
    return <p>Loading profile...</p>;
  }

  return (
    <div className="profile-container">
      <h2>Profile</h2>
      <div className="profile-card">
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Mobile:</strong> {user.mobile}</p>
        <p><strong>Vehicle 1:</strong> {user.VehicleNumber1}</p>
        <p><strong>Vehicle 2:</strong> {user.VehicleNumber2 || "Not Provided"}</p>
        <p><strong>Vehicle 3:</strong> {user.VehicleNumber3 || "Not Provided"}</p>
        <div className="profile-actions">
          <button onClick={() => console.log("Edit Profile")}>Edit Profile</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
