import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './ActiveBookingsPage.css';

const ActiveBookings = () => {
  const [activeBookings, setActiveBookings] = useState([]);
  const navigate = useNavigate();
  const MAX_TIME = 120; // Default remaining time in seconds (30 minutes)

  useEffect(() => {
    console.log('Fetching active bookings...');
    const fetchActiveBookings = async () => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId');

      if (!token || !userId) {
        navigate('/login');
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5000/activebooking?userId=${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (Array.isArray(response.data)) {
          console.log('✅ Active bookings fetched:', response.data);

          // Ensure `remaining_time` is handled properly
          const bookingsWithTimers = response.data.map((booking) => ({
            ...booking,
            remainingTime: booking.remaining_time || MAX_TIME, // Use fetched time or default
          }));

          setActiveBookings(bookingsWithTimers);
        } else {
          console.error('❌ Invalid response format:', response.data);
        }
      } catch (error) {
        console.error('❌ Error fetching active bookings:', error);
      }
    };

    fetchActiveBookings();
  }, [navigate]);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveBookings((prevBookings) =>
        prevBookings.map((booking) => {
          if (booking.remainingTime > 0) {
            return { ...booking, remainingTime: booking.remainingTime - 1 };
          } else if (booking.remainingTime === 0 && booking.status !== 0) {
            // ⬇ Call API when time reaches 0
            updateReservationStatus(booking.id);
            return { ...booking, status: 0 }; // Update local state
          }
          return booking;
        })
      );
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // ⬇ API to update reservation status in the backend
  const updateReservationStatus = async (bookingId) => {
    try {
      await axios.put(`http://localhost:5000/updateReservation/${bookingId}`, {
        status: 0, // Set status to 0 when time expires
      });
    } catch (error) {
      console.error('❌ Error updating reservation status:', error);
    }
  };

  const handleExtend = async (bookingId) => {
    try {
      const response = await axios.put(`http://localhost:5000/activebooking/extend/${bookingId}`);
  
      if (response.data.success) {
        setActiveBookings((prevBookings) =>
          prevBookings.map((booking) =>
            booking.id === bookingId
              ? { ...booking, remainingTime: MAX_TIME } // Reset to 30 minutes
              : booking
          )
        );
      } else {
        console.error('❌ Failed to extend booking:', response.data);
      }
    } catch (error) {
      console.error('❌ Error extending the booking:', error);
    }
  };
  

  const handleCancel = async (bookingId) => {
    try {
      await axios.delete(`http://localhost:5000/api/reservations/${bookingId}`);
      setActiveBookings((prevBookings) => prevBookings.filter((booking) => booking.id !== bookingId));
    } catch (error) {
      console.error('❌ Error canceling the booking:', error);
    }
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

const DEFAULT_MAP_LINK = "https://maps.app.goo.gl/nYzzBJPXvH3m9mZSA";

const handleViewMap = (mapLink) => {
  const url = mapLink || DEFAULT_MAP_LINK;
  window.open(url, "_blank"); // Opens in a new tab
};

  return (
    <div className="active-bookings-container">
      <h2>Active Bookings</h2>
      {activeBookings.length === 0 ? (
        <p>No active bookings</p>
      ) : (
        activeBookings.map((booking) => (
          <div key={booking.id} className="booking-card">
            <div className="booking-details">
              <h3>Booking ID: {booking.id}</h3>
              <p><strong>Slot:</strong> {booking.slot_id || 'N/A'}</p>
              <p><strong>Location:</strong> {booking.location || 'N/A'}</p>
              <p><strong>Vehicle:</strong> {booking.vehicle_number || 'N/A'}</p>
            </div>
            <div className="booking-timer">
              <CircularProgressbar
                value={(booking.remainingTime / MAX_TIME) * 100}
                text={formatTime(booking.remainingTime)}
                styles={buildStyles({
                  textSize: '18px',
                  pathColor: booking.remainingTime > 300 ? '#28a745' : '#dc3545',
                  textColor: '#333',
                  trailColor: '#f0f0f0',
                })}
              />
            </div>
            <div className="booking-actions">
              <button className="extend-btn" onClick={() => handleExtend(booking.id)}>Extend</button>
              <button className="cancel-btn" onClick={() => handleCancel(booking.id)}>Cancel</button>
              <button className="map-btn" onClick={() => handleViewMap(booking.map_link)}>View Map</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default ActiveBookings;
