import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ReservationPage.css';

const ReservationPage = ({ location, slot }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [vehicleNumber, setVehicleNumber] = useState('');
  const [reservationDate, setReservationDate] = useState('');
  
  useEffect(() => {
    // Fetch user profile details (assuming it's already fetched during login)
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get("http://localhost:5000/routes/profile", {
          headers: { Authorization: `Bearer ${token}` }
        })
        .then((response) => {
          setUserDetails(response.data.user); // Assuming 'user' contains details
        })
        .catch((error) => {
          console.error("Error fetching user details", error);
        });
    }
  }, []);

  const handleReserve = () => {
    // Handle reservation logic (e.g., post request to backend)
    console.log("Reservation details:", {
      vehicleNumber,
      reservationDate,
      location,
      slot
    });
  };

  return (
    <div className="reservation-page">
      <div className="reservation-box">
        <h2>Reserve Your Slot</h2>
        {userDetails && (
          <div className="user-info">
            <p><strong>Name:</strong> {userDetails.name}</p>
            <p><strong>Email:</strong> {userDetails.email}</p>
            <p><strong>Vehicle:</strong></p>
            <select 
              value={vehicleNumber} 
              onChange={(e) => setVehicleNumber(e.target.value)}
            >
              <option value="">Select Vehicle</option>
              <option value={userDetails.VehicleNumber1}>{userDetails.VehicleNumber1}</option>
              {userDetails.VehicleNumber2 && <option value={userDetails.VehicleNumber2}>{userDetails.VehicleNumber2}</option>}
              {userDetails.VehicleNumber3 && <option value={userDetails.VehicleNumber3}>{userDetails.VehicleNumber3}</option>}
            </select>
            <p><strong>Location:</strong> {location.name}</p>
            <p><strong>Slot Number:</strong> {slot.name}</p>
            <label htmlFor="reservation-date">Select Date and Time:</label>
            <input 
              type="datetime-local" 
              id="reservation-date" 
              value={reservationDate} 
              onChange={(e) => setReservationDate(e.target.value)}
            />
            <button onClick={handleReserve}>Reserve</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReservationPage;
