import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LoginPage.css";
import backgroundImg from "../images/img1.webp";

const LoginPage = () => {
  const [formData, setFormData] = useState({
    identifier: "",
    password: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleLogin = async () => {
    if (formData.identifier && formData.password) {
      try {
        const response = await fetch("http://localhost:5000/routes/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });

        const data = await response.json();
        console.log(data);

        if (response.ok) {
          console.log("Login successful, redirecting...");
          alert("Login successful!");
          localStorage.setItem("username", data.username);
          navigate("/Dashboard"); // Navigate to the homepage
        } else {
          console.error("Login failed with error:", data.error);
          alert(data.error || "Invalid credentials.");
        }
      } catch (error) {
        console.error("Error logging in:", error);
        alert("Something went wrong. Please check the console for details.");
      }
    } else {
      alert("Please fill in both fields.");
    }
  };

  return (
    <div
      className="login-page"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      <div className="form-container">
        <h1 className="form-title">Login</h1>
        <form>
          <input
            type="text"
            name="identifier"
            placeholder="Email or Mobile Number"
            value={formData.identifier}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button
            type="button"
            onClick={handleLogin}
            className="animated-btn login-btn"
          >
            Login
          </button>
        </form>
        <button onClick={() => navigate("/")}>Test Navigate</button>
      </div>
    </div>
  );
};

export default LoginPage;
