import React, { useState } from "react";
import "./LoginPage.css";
import backgroundImg from '../images/img1.webp';

const LoginPage = () => {
    const [formData, setFormData] = useState({
        identifier: "", // Email or mobile number
        password: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleLogin = async () => {
        if (formData.identifier && formData.password) {
            try {
                const response = await fetch("http://localhost:5000/api/login", {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(formData),
                });

                const data = await response.json();

                if (response.ok) {
                    alert("Login successful!"); // Alert for successful login
                } else {
                    alert(data.error || "Invalid credentials."); // Alert for failed login
                }
            } catch (error) {
                console.error("Error logging in:", error);
                alert("Something went wrong. Please try again.");
            }
        } else {
            alert("Please fill in both fields.");
        }
    };

    return (
        <div className="login-page" style={{ backgroundImage: `url(${backgroundImg})` }}>
            <div className="form-container">
                <h1 className="form-title">Login</h1>
                <form>
                    <input
                        type="text"
                        name="identifier"
                        placeholder="Email or Mobile Number"
                        value={formData.identifier}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <button type="button" onClick={handleLogin} className="animated-btn login-btn">
                        Login
                    </button>
                </form>
            </div>
        </div>
    );
};

export default LoginPage;
