import React, { useState } from "react";
import "./SignupPage.css"; // Updated CSS for styling
import backgroundImg from "../images/img1.webp";

const SignupPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    mobile: "",
    otp: "",
  });
  const [otpSent, setOtpSent] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSendOtp = () => {
    if (formData.email || formData.mobile) {
      // Simulate OTP sending logic
      alert("OTP sent successfully!");
      setOtpSent(true);
    } else {
      alert("Please provide an email or mobile number.");
    }
  };

  const handleSubmitOtp = () => {
    if (formData.otp) {
      alert("OTP verified successfully!");
    } else {
      alert("Please enter the OTP.");
    }
  };

  return (
    <div
      className="signup-page"
      style={{ backgroundImage: `url(${backgroundImg})` }}
    >
      <div className="form-container">
        <h1 className="form-title">Create Your Account</h1>
        <form>
          <div className="input-group">
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="email"
              name="email"
              placeholder="Email ID"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="tel"
              name="mobile"
              placeholder="Mobile Number"
              value={formData.mobile}
              onChange={handleChange}
              required
            />
          </div>
          <button
            type="button"
            onClick={handleSendOtp}
            className="animated-btn send-otp-btn"
          >
            Send OTP
          </button>
          {otpSent && (
            <div className="otp-section">
              <p className="otp-text">
                Enter the OTP sent to your email/mobile:
              </p>
              <div className="input-group">
                <input
                  type="text"
                  name="otp"
                  placeholder="Enter OTP"
                  value={formData.otp}
                  onChange={handleChange}
                  required
                />
              </div>
              <button
                type="button"
                onClick={handleSubmitOtp}
                className="animated-btn submit-otp-btn"
              >
                Submit OTP
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default SignupPage;
