import React, { useState } from "react";
import "./HomePage.css";
import SignupPage from "./Signup";
import LoginPage from "./Login";
import WelcomeImage from "../images/img1.webp"; // Replace with your image path
import Logo from "../images/logo.webp"; // Replace with your logo path

const HomePage = () => {
  const [activePage, setActivePage] = useState(null);

  const renderContent = () => {
    if (activePage === "login") {
      return <LoginPage />;
    } else if (activePage === "signup") {
      return <SignupPage />;
    } else {
      return (
        <div className="welcome-section">
          <div className="welcome-text animated fadeInUp">
            <h1 className="animated fadeInDown">Welcome to SmartPark</h1>
            <p className="animated fadeIn">Effortless Parking at Your Fingertips</p>
            <ul className="animated fadeIn">
              <li>
                <strong>Real-Time Parking Availability:</strong> Instantly view
                available parking spots around your destination.
              </li>
              <li>
                <strong>Advance Reservations:</strong> Reserve your parking space ahead of time to save effort and avoid last-minute stress.
              </li>
              <li>
                <strong>Guided Navigation:</strong> Get step-by-step guidance to seamlessly reach your reserved spot.
              </li>
            </ul>
            <p className="animated fadeIn">
              Join the revolution in parking managementâ€”saving time, reducing
              hassle, and making parking smarter.
            </p>
            <button
              className="cta-button animated fadeInUp"
              aria-label="Sign up for SmartPark"
              onClick={() => setActivePage("signup")}
            >
              Get Started
            </button>
          </div>
          <div className="welcome-image animated fadeInRight">
            <img src={WelcomeImage} alt="Smart Parking" />
          </div>
        </div>
      );
    }
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <img src={Logo} alt="SmartPark Logo" className="navbar-logo" />
        <div className="navbar-links">
          <button
            className={`navbar-btn ${activePage === null ? "active" : ""}`}
            aria-label="Go to Home page"
            onClick={() => setActivePage(null)}
          >
            Home
          </button>
          <button
            className={`navbar-btn ${activePage === "login" ? "active" : ""}`}
            aria-label="Go to Login page"
            onClick={() => setActivePage("login")}
          >
            Login
          </button>
          <button
            className={`navbar-btn ${activePage === "signup" ? "active" : ""}`}
            aria-label="Go to Signup page"
            onClick={() => setActivePage("signup")}
          >
            Signup
          </button>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default HomePage;
