import React, { useState } from "react";
import "./SignupPage.css";
import backgroundImg from "../images/img1.webp";

const SignupPage = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    mobile: "",
    otp: "",
  });
  const [otpSent, setOtpSent] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSendOtp = async () => {
    if (!formData.email) {
      alert("Please provide a valid email.");
      return;
    }
    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/auth/send-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: formData.email }),
      });
      const data = await response.json();
      setLoading(false);
      if (response.ok) {
        alert(data.message);
        setOtpSent(true);
      } else {
        alert(data.error || "Failed to send OTP");
      }
    } catch (error) {
      setLoading(false);
      console.error("Error sending OTP:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  const handleSubmitOtp = async () => {
    if (!formData.otp) {
      alert("Please enter the OTP.");
      return;
    }
    setLoading(true);
    try {
      const response = await fetch("http://localhost:5000/api/auth/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: formData.email, otp: formData.otp }),
      });
      const data = await response.json();
      setLoading(false);
      if (response.ok) {
        alert(data.message);
      } else {
        alert(data.error || "Failed to verify OTP");
      }
    } catch (error) {
      setLoading(false);
      console.error("Error verifying OTP:", error);
      alert("An error occurred. Please try again later.");
    }
  };

  return (
    <div className="signup-page" style={{ backgroundImage: `url(${backgroundImg})` }}>
      <div className="form-container">
        <h1 className="form-title">Signup</h1>
        <form>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email ID"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <input
            type="tel"
            name="mobile"
            placeholder="Mobile Number"
            value={formData.mobile}
            onChange={handleChange}
            required
          />
          <div className="button-container">
            <button
              type="button"
              onClick={handleSendOtp}
              className="animated-btn send-otp-btn"
              disabled={loading}
            >
              {loading ? "Sending OTP..." : "Send OTP"}
            </button>
          </div>
          {otpSent && (
            <div className="otp-section">
              <input
                type="text"
                name="otp"
                placeholder="Enter OTP"
                value={formData.otp}
                onChange={handleChange}
                required
              />
              <button
                type="button"
                onClick={handleSubmitOtp}
                className="animated-btn submit-otp-btn"
                disabled={loading}
              >
                {loading ? "Verifying OTP..." : "Submit OTP"}
              </button>
            </div>
          )}
        </form>
      </div>
    </div>
  );
};

export default SignupPage;
