import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./SlotBookingPage.css";

const SlotBookingPage = () => {
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate(); // For navigation

  // Fetch parking data from the backend
  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/locations");
        if (!response.ok) {
          throw new Error("Failed to fetch locations");
        }
        const data = await response.json();
        console.log(data);
        setLocations(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchLocations();
  }, []);

  const handleLocationSelect = (location) => {
    navigate(`/slot-details/${location.id}`, { state: location }); // Pass location data to the slot-details page
  };

  if (loading) {
    return <div>Loading locations...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="slot-booking-page">
      <h2>Select your location for slot booking</h2>
      <div className="locations-list">
        {locations.map((location) => (
          <div
            key={location.id}
            className="location-card"
            onClick={() => handleLocationSelect(location)}
          >
            <h3>{location.name}</h3>
            <div className="slot-info">
              <div className="slot-field">
                <strong>No. of Slots Vacant:</strong> {location.vacant}
              </div>
              <div className="slot-field">
                <strong>Occupied:</strong> {location.occupied}
              </div>
              <div className="slot-field">
                <strong>Reserved:</strong> {location.reserved}
              </div>
            </div>
          </div>
        ))}
      </div>
      {locations.length === 0 && <p>No locations available.</p>}
    </div>
  );
};

export default SlotBookingPage;
