import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ReservationPage.css'; // Add your styles here

const ReservationPage = () => {
  const { state } = useLocation(); // Access passed state
  const navigate = useNavigate();
  
  // If state is undefined or missing, show loading message
  if (!state || !state.slot || !state.location) {
    return <div>Loading...</div>;
  }

  const { slot, location } = state;
  const [vehicleNumber, setVehicleNumber] = useState('');
  const [reservationTime, setReservationTime] = useState('');
  const [vehicles, setVehicles] = useState([]);

  // Assuming you have a backend API to get the vehicle numbers associated with the user
  useEffect(() => {
    const fetchUserVehicles = async () => {
      try {
        const response = await axios.get('http://localhost:5000/routes/vehicles');
        setVehicles(response.data); // Assuming the response is an array of vehicle numbers
      } catch (error) {
        console.error('Error fetching vehicle numbers:', error);
      }
    };
    fetchUserVehicles();
  }, []);

  const handleSubmit = async () => {
    try {
      const response = await axios.post('http://localhost:5000/routes/reserve', {
        slot: slot.name,
        location: location.name,
        vehicleNumber,
        reservationTime,
      });
      alert('Reservation successful');
      navigate('/dashboard'); // Redirect to dashboard or another page after successful reservation
    } catch (error) {
      console.error('Error making reservation:', error);
      alert('Error during reservation');
    }
  };

  return (
    <div className="reservation-page">
      <h2>Reserve Slot {slot.name} at {location.name}</h2>
      <div>
        <label htmlFor="vehicleNumber">Select Vehicle</label>
        <select
          id="vehicleNumber"
          value={vehicleNumber}
          onChange={(e) => setVehicleNumber(e.target.value)}
        >
          <option value="">--Select Vehicle--</option>
          {vehicles.map((vehicle, index) => (
            <option key={index} value={vehicle}>
              {vehicle}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="reservationTime">Select Time</label>
        <input
          type="datetime-local"
          id="reservationTime"
          value={reservationTime}
          onChange={(e) => setReservationTime(e.target.value)}
        />
      </div>
      <button onClick={handleSubmit}>Reserve Slot</button>
    </div>
  );
};

export default ReservationPage;
