import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [form, setForm] = useState({ username: '', email: '', password: '', mobile: '' });
  const [otpSent, setOtpSent] = useState(false);
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Send the signup request to the server
      const res = await axios.post('http://localhost:5000/api/auth/signup', form);
      alert(res.data.message);
      setOtpSent(true); // Show OTP input once the OTP is sent
    } catch (err) {
      console.error('Error:', err);
      alert('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    // You can now verify the OTP on the backend (implement this part in backend)
    console.log('OTP:', otp);
    // Call backend to verify OTP here
  };

  return (
    <div style={{ textAlign: 'center', margin: '20px' }}>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="email"
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="password"
            type="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="mobile"
            type="tel"
            placeholder="Mobile Number"
            value={form.mobile}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div>
          <button
            type="submit"
            style={{
              padding: '10px 20px',
              backgroundColor: '#007BFF',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
            disabled={loading}
          >
            {loading ? 'Signing Up...' : 'Signup'}
          </button>
        </div>
      </form>

      {otpSent && (
        <form onSubmit={handleOtpSubmit} style={{ marginTop: '20px' }}>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            style={{ padding: '10px', width: '300px' }}
            required
          />
          <button
            type="submit"
            style={{
              padding: '10px 20px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Verify OTP
          </button>
        </form>
      )}
    </div>
  );
};

export default Signup;
