import React, { useState } from "react";
import "./HomePage.css";
import SignupPage from "./Signup";
import LoginPage from "./Login";

const HomePage = () => {
  const [activePage, setActivePage] = useState("home"); // Default to 'home'

  const renderContent = () => {
    if (activePage === "login") {
      return <LoginPage />;
    } else if (activePage === "signup") {
      return <SignupPage />;
    } else {
      return (
        <div className="intro-section">
          <div className="intro-text">
            <h1>Welcome to MyApp</h1>
            <p>
              Discover the future with us! MyApp is a cutting-edge platform designed to revolutionize the way you connect, collaborate, and create. 
              Explore our features and join us in shaping a brighter tomorrow.
            </p>
          </div>
          <div className="intro-image">
            <img src="../images/img1.webp" alt="Who We Are" />
          </div>
        </div>
      );
    }
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <h1 className="navbar-logo">MyApp</h1>
        <div className="navbar-links">
          <button
            className={`navbar-btn ${activePage === "home" ? "active" : ""}`}
            onClick={() => setActivePage("home")}
          >
            Home
          </button>
          <button
            className={`navbar-btn ${activePage === "login" ? "active" : ""}`}
            onClick={() => setActivePage("login")}
          >
            Login
          </button>
          <button
            className={`navbar-btn ${activePage === "signup" ? "active" : ""}`}
            onClick={() => setActivePage("signup")}
          >
            Signup
          </button>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default HomePage;
