import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Profile.css";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [updatedUser, setUpdatedUser] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
  if (isEditing) {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  }
}, [isEditing]);
  useEffect(() => {
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");

    if (!token || !userId) {
      navigate("/login");
      return;
    }

    axios
      .get(`http://localhost:5000/routes/eprofile/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => {
        setUser(response.data.user);
        setUpdatedUser(response.data.user); // Store initial user data
      })
      .catch((error) => {
        console.error("Error fetching profile:", error);
        navigate("/login");
      });
  }, [navigate]);

  const handleChange = (e) => {
    setUpdatedUser({ ...updatedUser, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    setLoading(true);
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");

    axios
      .put(`http://localhost:5000/routes/update-profile/${userId}`, updatedUser, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        setUser(updatedUser); // Update state with new user data
        setIsEditing(false); // Exit edit mode
      })
      .catch((error) => {
        console.error("Error updating profile:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  if (!user) {
    return <p>Loading profile...</p>;
  }

  return (
  <div className="profile-container">
    <h2>Account Profile</h2>
    <div className="profile-card">
      {isEditing ? (
        <div className="edit-fields scroll-container">
          <div className="profile-field">
            <label className="input-label">Full Name</label>
            <input
              type="text"
              name="name"
              value={updatedUser.name}
              onChange={handleChange}
            />
          </div>
          <div className="profile-field">
            <label className="input-label">Email Address</label>
            <input
              type="email"
              name="email"
              value={updatedUser.email}
              onChange={handleChange}
            />
          </div>
          <div className="profile-field">
            <label className="input-label">Mobile Number</label>
            <input
              type="text"
              name="mobile"
              value={updatedUser.mobile}
              onChange={handleChange}
            />
          </div>
          <div className="profile-field">
            <label className="input-label">Vehicle 1</label>
            <input
              type="text"
              name="vehicle_number1"
              value={updatedUser.vehicle_number1}
              onChange={handleChange}
            />
          </div>
          <div className="profile-field">
            <label className="input-label">Vehicle 2</label>
            <input
              type="text"
              name="vehicle_number2"
              value={updatedUser.vehicle_number2 || ""}
              onChange={handleChange}
            />
          </div>
          <div className="profile-field">
            <label className="input-label">Vehicle 3</label>
            <input
              type="text"
              name="vehicle_number3"
              value={updatedUser.vehicle_number3 || ""}
              onChange={handleChange}
            />
          </div>
        </div>
        ) : (
          <div className="profile-info">
            <div className="profile-field">
              <p><strong>Name:</strong> <span>{user.name}</span></p>
            </div>
            <div className="profile-field">
              <p><strong>Email:</strong> <span>{user.email}</span></p>
            </div>
            <div className="profile-field">
              <p><strong>Mobile:</strong> <span>{user.mobile}</span></p>
            </div>
            <div className="profile-field">
              <p><strong>Vehicle 1:</strong> <span>{user.vehicle_number1}</span></p>
            </div>
            <div className="profile-field">
              <p><strong>Vehicle 2:</strong> <span>{user.vehicle_number2 || "Not Provided"}</span></p>
            </div>
            <div className="profile-field">
              <p><strong>Vehicle 3:</strong> <span>{user.vehicle_number3 || "Not Provided"}</span></p>
            </div>
          </div>
        )}
  
        <div className="profile-actions">
          {isEditing ? (
            <>
              <button 
                className="save-btn" 
                onClick={handleSave} 
                disabled={loading}
              >
                {loading ? (
                  <>
                    <span className="loading-spinner"></span>
                    Saving Changes
                  </>
                ) : 'Save Changes'}
              </button>
              <button className="cancel-btn" onClick={() => setIsEditing(false)}>
                Cancel
              </button>
            </>
          ) : (
            <button className="edit-btn" onClick={() => setIsEditing(true)}>
              ‚úèÔ∏è Edit Profile
            </button>
          )}
          <button className="logout-btn" onClick={handleLogout}>
            üîí Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
