import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Navigation
import axios from "axios"; // API Requests
import "./Dashboard.css";
import SignupPage from "./Signup";
import LoginPage from "./Login";
import SlotBookingPage from "./SlotBookingPage";
import BookingHistoryPage from "./bookingHistoryPage";
import WelcomeImage from "../images/img2.webp";
import Logo from "../images/logo.webp";

const Dashboard = () => {
  const [activePage, setActivePage] = useState(null);
  const [typedText, setTypedText] = useState("");
  const [user, setUser] = useState(null); // Store user profile data
  const [activeReservation, setActiveReservation] = useState(null);
  const [remainingTime, setRemainingTime] = useState(null);
  const navigate = useNavigate();
  const fullText = "Welcome to SmartParking!!!";

  // Typing Effect
  useEffect(() => {
    let index = 0;
    const typingInterval = setInterval(() => {
      if (index < fullText.length) {
        setTypedText(fullText.slice(0, index + 1));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100);
    return () => clearInterval(typingInterval);
  }, []);

  // Fetch user profile after login & store user ID
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      axios
        .get("http://localhost:5000/routes/profile", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setUser(response.data.user);
          localStorage.setItem("userId", response.data.user.id);
        })
        .catch((error) => {
          console.error("Error fetching profile:", error);
          localStorage.removeItem("token");
          localStorage.removeItem("userId");
          navigate("/login");
        });
    } else {
      navigate("/login");
    }
  }, [navigate]);

  // Fetch active reservation on page load
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      axios
        .get("http://localhost:5000/routes/active-reservation", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          if (response.data.active) {
            setActiveReservation(response.data.reservation);
            setRemainingTime(response.data.remainingTime);
          } else {
            setActiveReservation(null);
          }
        })
        .catch((error) => {
          console.error("Error fetching active reservation:", error);
        });
    }
  }, []);

  // Countdown Timer for Remaining Time
  useEffect(() => {
    if (remainingTime !== null && remainingTime > 0) {
      const interval = setInterval(() => {
        setRemainingTime((prevTime) => (prevTime > 0 ? prevTime - 1000 : 0));
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [remainingTime]);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setUser(null);
    navigate("/");
  };

  // Extend Reservation
  const handleExtendReservation = () => {
    axios
      .post(
        "http://localhost:5000/routes/extend-reservation",
        { userId: localStorage.getItem("userId") },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      )
      .then((response) => {
        setRemainingTime(response.data.newRemainingTime);
        alert("Reservation extended successfully!");
      })
      .catch((error) => {
        console.error("Error extending reservation:", error);
        alert("Could not extend reservation.");
      });
  };

  // Cancel Reservation
  const handleCancelReservation = () => {
    axios
      .post(
        "http://localhost:5000/routes/cancel-reservation",
        { userId: localStorage.getItem("userId") },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      )
      .then(() => {
        setActiveReservation(null);
        alert("Reservation canceled successfully!");
      })
      .catch((error) => {
        console.error("Error canceling reservation:", error);
        alert("Could not cancel reservation.");
      });
  };

  // Dynamic Content Rendering
  const renderContent = () => {
    if (activePage === "login") return <LoginPage />;
    if (activePage === "signup") return <SignupPage />;
    if (activePage === "slotBooking") return <SlotBookingPage />;
    if (activePage === "history") return <BookingHistoryPage />;
    
    if (activeReservation) {
      return (
        <div className="reservation-card">
          <h2>Active Reservation</h2>
          <p><strong>Slot:</strong> {activeReservation.slotNumber}</p>
          <p><strong>Time Left:</strong> {Math.floor(remainingTime / 60000)} min {Math.floor((remainingTime % 60000) / 1000)} sec</p>
          <div className="reservation-actions">
            <button onClick={() => navigate("/map")}>View Map</button>
            <button onClick={handleExtendReservation}>Extend</button>
            <button onClick={handleCancelReservation} className="cancel-btn">Cancel</button>
          </div>
        </div>
      );
    }

    return (
      <div className="welcome-section">
        <div className="welcome-text">
          <h1 className="typing-effect">{typedText}</h1>
          <p className="fade-in">
            Discover a smarter way to park. Save time, reduce hassle, and enjoy effortless parking at your fingertips.
          </p>
          <button onClick={() => setActivePage("signup")}>Get Started</button>
        </div>
        <div className="welcome-image">
          <img src={WelcomeImage} alt="Smart Parking" />
        </div>
      </div>
    );
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <img src={Logo} alt="SmartPark Logo" className="navbar-logo" />
        <div className="navbar-links">
          <button className={`navbar-btn ${activePage === null ? "active" : ""}`} onClick={() => setActivePage(null)}>
            Home
          </button>
          <button className={`navbar-btn ${activePage === "slotBooking" ? "active" : ""}`} onClick={() => setActivePage("slotBooking")}>
            Slot Booking
          </button>
          <button className={`navbar-btn ${activePage === "history" ? "active" : ""}`} onClick={() => setActivePage("history")}>
            History
          </button>
          <div className="user-details-dropdown">
            <button className="navbar-btn user-btn">
              {user ? `Welcome, ${user.name}` : "User Details"} <i className="fas fa-user"></i>
            </button>
            <div className="dropdown-content">
              {user ? (
                <>
                  <button onClick={() => setActivePage("profile")}>Profile</button>
                  <button onClick={handleLogout}>Logout</button>
                </>
              ) : (
                <p>Please log in</p>
              )}
            </div>
          </div>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default Dashboard;
