import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [form, setForm] = useState({ username: '', email: '', password: '', mobile: '' });
  const [otp, setOtp] = useState('');
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [loading, setLoading] = useState(false); // To show loading state

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state
    try {
      const res = await axios.post('http://localhost:5000/api/auth/signup', form);
      console.log('Response:', res); // Debugging response
      if (res.data && res.data.message) {
        alert(res.data.message); // Show success message
        setIsOtpSent(true); // OTP is sent, show the OTP verification form
      } else {
        alert('Unexpected response from server.');
      }
    } catch (err) {
      console.error('Error:', err); // Debugging error
      alert(err.response?.data?.error || 'Something went wrong');
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  const handleOtpVerify = async (e) => {
    e.preventDefault();
    setLoading(true); // Show loading state
    try {
      const res = await axios.post('http://localhost:5000/api/auth/verify-otp', {
        email: form.email,
        otp,
        username: form.username,
        password: form.password,
        mobile: form.mobile,
      });
      if (res.data && res.data.message) {
        alert(res.data.message); // Show success message
      } else {
        alert('Unexpected response from server.');
      }
    } catch (err) {
      console.error('Error:', err); // Debugging error
      alert(err.response?.data?.error || 'OTP verification failed');
    } finally {
      setLoading(false); // Hide loading state
    }
  };

  return (
    <div style={{ textAlign: 'center', margin: '20px' }}>
      <h2>Signup</h2>
      <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="email"
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="password"
            type="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input
            name="mobile"
            type="tel"
            placeholder="Mobile Number"
            value={form.mobile}
            onChange={handleChange}
            style={{ padding: '10px', width: '300px' }}
            required
          />
        </div>
        <div>
          <button
            type="submit"
            style={{
              padding: '10px 20px',
              backgroundColor: '#007BFF',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
            disabled={loading} // Disable button during loading
          >
            {loading ? 'Signing Up...' : 'Signup'}
          </button>
        </div>
      </form>

      {isOtpSent && (
        <form onSubmit={handleOtpVerify} style={{ marginTop: '20px' }}>
          <h3>Verify OTP</h3>
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
            style={{ padding: '10px', width: '300px' }}
            required
          />
          <div style={{ marginTop: '10px' }}>
            <button
              type="submit"
              style={{
                padding: '10px 20px',
                backgroundColor: '#28a745',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
              disabled={loading} // Disable button during loading
            >
              {loading ? 'Verifying OTP...' : 'Verify OTP'}
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default Signup;
