import React, { useState } from "react";
import "./HomePage.css";
import SignupPage from "./Signup";
import LoginPage from "./Login";
import WelcomeImage from "../images/img1.webp";
import Logo from "../images/logo.webp"; // Import your logo image

const HomePage = () => {
  const [activePage, setActivePage] = useState(null);

  const renderContent = () => {
    if (activePage === "login") {
      return <LoginPage />;
    } else if (activePage === "signup") {
      return <SignupPage />;
    } else {
      return (
        <div className="welcome-section">
          <div className="welcome-text">
            <h1>Welcome to MyApp</h1>
            <p>
              Discover a world of possibilities with MyApp. We are here to
              revolutionize the way you connect, interact, and achieve your goals.
            </p>
            <button onClick={() => setActivePage("signup")}>Get Started</button>
          </div>
          <div className="welcome-image">
            <img src={WelcomeImage} alt="Who We Are" />
          </div>
        </div>
      );
    }
  };

  return (
    <div className="home-page">
      <nav className="navbar">
        <img src={Logo} alt="MyApp Logo" className="navbar-logo" /> {/* Use logo here */}
        <div className="navbar-links">
          <button
            className={`navbar-btn ${activePage === null ? "active" : ""}`}
            onClick={() => setActivePage(null)}
          >
            Home
          </button>
          <button
            className={`navbar-btn ${activePage === "login" ? "active" : ""}`}
            onClick={() => setActivePage("login")}
          >
            Login
          </button>
          <button
            className={`navbar-btn ${activePage === "signup" ? "active" : ""}`}
            onClick={() => setActivePage("signup")}
          >
            Signup
          </button>
        </div>
      </nav>
      <div className="content-area">{renderContent()}</div>
    </div>
  );
};

export default HomePage;
